mapper.sql:
( SELECT ROW_NUMBER() OVER(ORDER BY t.bzmn) sn , COALESCE(t.co_nm, '합계') co_nm , SUM(t.rslt_cd_00) cd00, SUM(t.rslt_cd_01) cd01, SUM(t.rslt_cd_02) cd02, SUM(t.rslt_cd_03) cd03, SUM(t.rslt_cd_04) cd04 , SUM(t.rslt_cd_11) cd11, SUM(t.rslt_cd_12) cd12, SUM(t.rslt_cd_13) cd13, SUM(t.rslt_cd_14) cd14, SUM(t.rslt_cd_21) cd21 , SUM(t.rslt_cd_22) cd22, SUM(t.rslt_cd_23) cd23, SUM(t.rslt_cd_24) cd24, SUM(t.rslt_cd_25) cd25, SUM(t.rslt_cd_31) cd31 , SUM(t.rslt_cd_51) cd51, SUM(t.sum) tot FROM ( SELECT COALESCE(agency.co_nm, '-') co_nm , CASE hs.bzmn_sn WHEN '-' THEN NULL WHEN '' THEN NULL ELSE hs.bzmn_sn END bzmn , hs.* FROM dqvs.dvs_st_verf_hour hs LEFT OUTER JOIN dqvs.dvs_rac_mt_agency agency ON hs.bzmn_sn = agency.bzmn_sn
WHERE agency.reg_cmptnc_cd = #{cmptncCd}
WHERE 1=1
AND EXTRACT(YEAR FROM hs.dt) = #{monthDt}::NUMERIC
AND hs.dt::DATE BETWEEN CONCAT(#{monthDt}, '-01')::DATE AND CONCAT(#{monthDt}, '-01')::DATE + INTERVAL '1 month' - INTERVAL '1 second'
AND hs.dt::DATE = #{monthDt}::DATE
AND hs.vrfc_mthd = #{verfMthd}
AND agency.bzmn_se_cd = #{auth}
AND agency.co_nm LIKE '%'||#{companyNm}||'%'
) AS t WHERE 1=1 GROUP BY ROLLUP((t.bzmn, t.co_nm)) ) AS T

mapper.select:
/* 자격검증통계 카운트(사업자별) */ SELECT * FROM
WHERE T.sn <= (CAST(#{take} AS INTEGER) + CAST(#{skip} AS INTEGER))
AND T.sn > CAST(#{skip} AS INTEGER)

