mapper.select:
select count(*) from( select vhcl_reg_no, vin, carmdl, mdlyr, vhcl_nm, bzmn_sn, engine_fom, use_yn, sgg_cd, ownr_nm, reg_dt from dqvs.dvs_rac_mt_car where use_yn='Y'
and vhcl_reg_no like '%' || #{carNum} || '%'
AND crno = #{crno}
AND 1 != 1
)a

select vin from dqvs.dvs_rac_mt_car where use_yn = 'Y'
and vhcl_reg_no = #{carNum}
limit 1

SELECT * FROM( SELECT T.* FROM ( SELECT row_number() over( order by vrfc_dmnd_dt desc) as SN , vrfc_hstry_sn , rqstr_sn , drma.co_nm , case WHEN vrfc_mthd = '4' THEN REPEAT('*', LENGTH(viui.sso_user_nm)) when viui.sso_user_nm is not null then concat(left(viui.sso_user_nm, 1), repeat('*', length(viui.sso_user_nm) - 2), right(viui.sso_user_nm, 1)) when viui.onepass_user_nm is not null then concat(left(viui.onepass_user_nm, 1), repeat('*', length(viui.onepass_user_nm) - 2), right(viui.onepass_user_nm, 1)) else null end as rqstr_nm , CONCAT(dqvs.pca_decrypt(ddhv.dln, '150'), '******') AS dln , lcns_asort_cd , CASE ddhv.lcns_asort_cd WHEN '11' THEN '1종대형' WHEN '12' THEN '1종보통' WHEN '13' THEN '1종소형' WHEN '14' THEN '대형견인' WHEN '15' THEN '구난차' WHEN '16' THEN '소형견인' WHEN '32' THEN '2종보통' WHEN '33' THEN '2종소형' WHEN '38' THEN '원동기' ELSE lcns_asort_cd END AS lcns_type , vrfc_dmnd_dt , rent_bgng_dt , rent_end_dt , (CASE vrfc_mthd WHEN '1' THEN '직접입력' WHEN '2' THEN 'OCR' WHEN '3' THEN '모바일면허증' when '4' then 'API' end) as vrfc_mthd , ddhv.vrfc_rslt_cd as verify_cd , CASE when ddhv.req_rslt_cd = '00'or ddhv.req_rslt_cd = '1' or ddhv.req_rslt_cd = '0' then dqvs.fn_get_cdnm('verify_cd', ddhv.vrfc_rslt_cd) else dqvs.fn_get_cdnm('req_rslt_cd', ddhv.req_rslt_cd) end as verify_nm , ddhv.req_rslt_cd , prcs_se , drma.bzmn_sn FROM dqvs.dvs_dqv_hs_verf AS ddhv LEFT OUTER JOIN dqvs.v_intg_user_info AS viui ON ddhv.rqstr_sn = viui.user_sn LEFT OUTER JOIN dqvs.dvs_rac_mt_agency AS drma ON viui.bzmn_sn = drma.bzmn_sn where ddhv.dln_enc = dqvs.pca_encrypt(#{dln}, '200') AND vrfc_dmnd_dt >= now() - interval '7days' ) T
WHERE T.SN > #{skip}
OFFSET ((#{page} - 1) * #{take})
WHERE T.SN > 0
LIMIT #{take}
LIMIT 50000
) AS TT

select count(1) from dqvs.dvs_rac_mt_car drmc where drmc.use_yn = 'Y'
and vhcl_reg_no = #{carNum}
AND crno = #{crno}
AND 1 != 1

select vhcl_reg_no, drmc.vin, carmdl, mdlyr, vhcl_nm, bzmn_sn, engine_fom, use_yn, sgg_cd, ownr_nm, to_char(reg_dt, 'yyyy-mm-dd') as reg_dt,
case when fvrts.reg_yn is null then 'N' else 'Y' end as reg_yn from dqvs.dvs_rac_mt_car drmc left join ( select vin , mdfr_sn, 'Y' as reg_yn from dqvs.dvs_rac_mt_fvrts ) as fvrts on drmc.vin = fvrts.vin where drmc.use_yn = 'Y'
and vhcl_reg_no = #{carNum}
AND crno = #{crno}
AND 1 != 1

select case when T.reg_yn is null then 'Y' else 'N' end as reg_yn from ( select vhcl_reg_no, drmc.vin, carmdl, mdlyr, vhcl_nm, bzmn_sn, engine_fom, use_yn, sgg_cd, ownr_nm, to_char(reg_dt, 'yyyy-mm-dd') as reg_dt, fvrts.reg_yn from dqvs.dvs_rac_mt_car drmc left join ( select vin , mdfr_sn, 'Y' as reg_yn from dqvs.dvs_rac_mt_fvrts ) as fvrts on drmc.vin = fvrts.vin where drmc.use_yn = 'Y'
and vhcl_reg_no = #{carNum}
AND crno = #{crno}
AND 1 != 1
) as T
