mapper.select:
SELECT bzmn_se_cd
FROM dqvs.dvs_rac_mt_agency
WHERE bzmn_sn = #{bzmnSn}

mapper.sql:
( SELECT ROW_NUMBER() OVER(ORDER BY T.reg_dt DESC) rn , * FROM ( SELECT rent.rent_no , rent.vhcl_reg_no , TO_CHAR(rent.rent_bgng_dt, 'YYYY-MM-DD HH24:MI') rent_bgng_dt , TO_CHAR(rent.rent_end_dt, 'YYYY-MM-DD HH24:MI') rent_end_dt , rent.rent_stts_cd , CASE rent.rent_stts_cd WHEN '1' THEN '미확정' WHEN '2' THEN '대여확정' WHEN '9' THEN '대여취소' ELSE rent.rent_stts_cd END rent_stts_nm , agency.co_nm , rent.vrfc_hstry_sn , rent.rgtr_sn , TO_CHAR(rent.reg_dt, 'YYYY-MM-DD HH24:MI') reg_dt , rent.mdfcn_dt , rent.dln FROM ( SELECT base.rent_no, base.vhcl_reg_no, base.bzmn_sn, base.rent_bgng_dt, base.rent_end_dt , base.rent_stts_cd, base.vrfc_hstry_sn, base.del_yn, base.rgtr_sn, base.reg_dt, base.mdfcn_dt, verf.dln FROM dqvs.dvs_dqv_mt_rent base LEFT JOIN dqvs.dvs_dqv_hs_verf verf ON verf.vrfc_hstry_sn = base.vrfc_hstry_sn WHERE (#{rentDt}::DATE BETWEEN base.rent_bgng_dt::DATE AND base.rent_end_dt::DATE) AND base.reg_dt <= NOW()
AND (base.reg_dt BETWEEN #{startDt}::TIMESTAMP AND #{endDt}::DATE + '23:59:59'::INTERVAL)
AND base.rent_stts_cd = #{rentSttsCd}
AND verf.dln_enc = dqvs.pca_encrypt(#{dln}, '200')
) AS rent LEFT OUTER JOIN dqvs.dvs_rac_mt_agency agency ON rent.bzmn_sn = agency.bzmn_sn WHERE 1=1
AND rent.del_yn = 'N'
AND rent.vhcl_reg_no LIKE '%' || #{vhclRegNo} || '%'
AND agency.co_nm LIKE '%' || #{coNm} || '%'
AND 1=1
AND agency.reg_cmptnc_cd LIKE #{cmptncZoneCd} || '%'
AND (rent.bzmn_sn IN (SELECT bzmn_sn FROM dqvs.dvs_rac_mt_agency WHERE up_brno = #{bzmnSn}) OR rent.bzmn_sn = #{bzmnSn})
AND rent.bzmn_sn = #{bzmnSn}
AND (rent.bzmn_sn IN (SELECT bzmn_sn FROM dqvs.dvs_rac_mt_agency WHERE up_brno = #{bzmnSn}) OR rent.bzmn_sn = #{bzmnSn})
AND rent.bzmn_sn = #{bzmnSn}
AND rent.bzmn_sn = #{bzmnSn}
) AS T ) AS A

