mapper.insert:
with max_his_number as(
	select
		MAX(rent_hstry_no) AS max_num
	from dqvs.dvs_dqv_hs_rent
	where rent_no = #{rentNo}
	group by rent_no
)
INSERT INTO dqvs.dvs_dqv_hs_rent
(
	rent_no
	, rent_hstry_no
	, vin
	, vhcl_reg_no
	, rent_prcs_dt
	, rent_bgng_dt
	, rent_end_dt
	, bzmn_sn
	, sgg_cd
	, vrfc_hstry_sn
	, rent_stts_cd
	, vhcl_idnty_cd
	, oneway_yn
	, use_yn
	, del_yn
	, rmrk
	, rgtr_sn
	, reg_dt
	, reg_ip
	, mdfr_sn
	, mdfcn_dt
	, mdfcn_ip

)
values
(
#{rentNo}
,CAST((SELECT max_num FROM max_his_number) AS numeric) + 1
,#{vin}
,#{vhclRegNo}
,#{rentPrcsDt}
,#{rentBgngDt}
,#{rentEndDt}
,#{bzmnSn}
,#{signguCd}
,'9'::numeric
,#{rentSttsCd}
,#{vhclIdntyCd}
,#{onewayYn}
,#{useYn}
,#{delYn}
,#{rmrk}
,#{userSn}::numeric
,now()
,#{ip}
,null
,null
,null

)

